const form = document.getElementById('form');
const input = document.getElementById('input');
const inbox = document.querySelector('.message_container');

clientData = {
    userId: user._id,
    Name: user.Username,
    Role: user.role,
    roomId: user._id
}

form.addEventListener('submit',function(event){
    event.preventDefault();
    const msg = input.value;

    if(msg){
        // message sent by client
        clientData.text = msg;
        clientData.managerId = user._id;
        socket.emit('message',clientData);
        input.value = '';
        input.focus();
    }
});
// message from server
socket.on('message',function(message){
    console.log(message);
    showMessage(message);
    inbox.scrollTop = inbox.scrollHeight;

});
socket.emit('joinUserRoom', clientData.userId);

// join the room
socket.emit('managerJoin',clientData.userId);

//showing the message in the chat box
function showMessage(message){
    const section = document.createElement('section');
    if(message.userId === user._id){
        section.classList.add('message_sent');
    }
    else{
        section.classList.add('message_received');
    }

    section.innerHTML = `                    <section class="profile">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="icon">
                            <path stroke-linecap="round" stroke-linejoin="round" d="M17.982 18.725A7.488 7.488 0 0 0 12 15.75a7.488 7.488 0 0 0-5.982 2.975m11.963 0a9 9 0 1 0-11.963 0m11.963 0A8.966 8.966 0 0 1 12 21a8.966 8.966 0 0 1-5.982-2.275M15 9.75a3 3 0 1 1-6 0 3 3 0 0 1 6 0Z" />
                        </svg>
                        <p class="user_name">${message.Name}</p>
                        <strong class="user_role">${message.Role}</strong>
                        <p class="message_time">${new Date().toLocaleTimeString([],{hour: '2-digit',minute:'2-digit',hour12:'false'})}</p>

                    </section>
                    <p class="text">${message.text}</p>`;
    document.querySelector('.message_container').appendChild(section);
}

cssCoverage.text:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background-color: #fdfdfd;
    color: #333;
    line-height: 1.1;
}

.chat_system {
    display: flex;
    flex-direction: row;
}

.inbox {
    display: flex;
    flex-direction: column;
    width: 70%;
    height: 90vh;
    border-radius: 8px;
    border: 1px solid #ccc;
    margin-top: 3%;
    margin-left: 3%;
    background-color: #fff;
    overflow: hidden;
    position: relative;
}


.inbox .heading {
    padding: 10px 16px;
    font-size: 1.2rem;
    font-weight: 600;
    background: #f5f5f5;
    border-bottom: 1px solid #ddd;
    position: sticky;
    top: 0;
    z-index: 10;
}


.message_container {
    flex: 1;
    overflow-y: auto;
    padding: 10px 16px;
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.message_received {
    align-self: flex-start;
    background: #e5e5ea;
    color: #000;
    border-bottom-left-radius: 4px;
}

 .message_received {
    padding: 10px 14px;
    border-radius: 18px;
    font-size: 0.95rem;
    line-height: 1.4;
    word-wrap: break-word;
    display: inline-block;
}
/* Container for each sent message */
.message_sent {
    display: flex;
    align-items: flex-start;
    margin: 10px 0;
    max-width: 80%;
    background-color: #426eff; 
    padding: 12px 15px;
    border-radius: 20px;
    box-shadow: 0 2px 6px rgba(0,0,0,0.1);
    position: relative;
    margin-left: auto; 
    flex-direction: column;
}


.message_sent .profile {
    display: flex;
    align-items: center;
    gap: 8px;
    margin-bottom: 8px;
}


.message_sent .profile .icon {
    width: 36px;
    height: 36px;
    color: #ffffff; 
}


.message_sent .profile .user_name {
    font-weight: 600;
    font-size: 14px;
    color: #ffffff;
}


.message_sent .profile .user_role {
    font-size: 14px;
    color: #000000;
    margin-left: 5px;
}

.message_sent .profile .message_time {
    font-size: 11px;
    color: #ffffff;
    margin-left: auto;
}


.message_sent .text {
    font-size: 18px;
    color: #ffffff;
    line-height: 1.4;
    word-wrap: break-word;
}


.message_sent::after {
    content: '';
    position: absolute;
    right: -8px;
    top: 20px;
    border-width: 8px 0 8px 8px;
    border-style: solid;
    border-color: transparent transparent transparent #e0f7fa;
}

/* Container for each sent message */
.message_received {
    display: flex;
    align-items: flex-end;
    margin: 10px 0;
    max-width: 80%;
    background-color: #d1d1d1; 
    padding: 12px 15px;
    border-radius: 20px;
    box-shadow: 0 2px 6px rgba(0,0,0,0.1);
    position: relative;
    margin-right: auto; 
    flex-direction: column;
}

.message_received .profile {
    display: flex;
    align-items: center;
    gap: 8px;
    margin-bottom: 8px;
}


.message_received .profile .icon {
    width: 36px;
    height: 36px;
    color: #001c79; 
}


.message_received .profile .user_name {
    font-weight: 600;
    font-size: 14px;
    color: #000000;
}

.message_received .profile .user_role {
    font-size: 14px;
    color: #00796b;
    margin-left: 5px;
}


.message_received .profile .message_time {
    font-size: 11px;
    color: #555;
    margin-left: auto;
}


.message_received .text {
    font-size: 18px;
    color: #000000;
    line-height: 1.4;
    word-wrap: break-word;
}


.message_received::after {
    content: '';
    position: absolute;
    left: -8px;
    top: 20px;
    border-width: 8px 0 8px 8px;
    border-style: solid;
    border-color: transparent transparent transparent #d1d1d1;
}


.input {
    display: flex;
    gap: 10px;
    padding: 10px;
    background: #f5f5f5;
    border-top: 1px solid #ddd;
    position: sticky;
    bottom: 0;
    z-index: 10;
}

.input_message {
    flex: 1;
    padding: 10px;
    border-radius: 25px;
    border: 1px solid #ccc;
    font-size: 1rem;
}

.input button {
    background-color: #000;
    border: none;
    color: white;
    border-radius: 50%;
    width: 40px;
    height: 40px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
}

.input button:hover {
    background-color: #fd7200;
}

.input button svg {
    width: 20px;
    height: 20px;
}


.members {
    display: flex;
    flex-direction: column;
    width: 30%;
    height: 90vh;
    border-radius: 8px;
    border: 1px solid #ccc;
    margin-top: 3%;
    margin-left: 3%;
    margin-right: 3%;
    background-color: #fff;
    padding: 10px;
    overflow-y: auto;
}

.members .profile {
    display: flex;
    align-items: center;
    gap: 10px;
    padding: 8px 0;
    border-bottom: 1px solid #eee;
    position: relative;
}


.members .profile:last-child {
    border-bottom: none;
}


.icon_frame {
    width: 40px;
    height: 40px;
    background-color: #e0f2f1;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
}


.icon_frame .icon {
    width: 24px;
    height: 24px;
    color: #2a5be0;
}


.members .user_name {
    font-size: 14px;
    font-weight: 600;
    color: #000000;
}

.members .user_role {
    font-size: 14px;
    color: #000000;
    margin-left: 5px;
}



@media (max-width: 768px) {
    .chat_system {
        flex-direction: column;
    }

    .inbox, .members {
        width: 100%;
        margin: 2% 0;
        height: auto;
    }

    .inbox {
        height: 70vh;
    }

    .members {
        max-height: 25vh;
    }

    .input_message {
        font-size: 0.9rem;
        padding: 8px;
    }

    .input button {
        width: 36px;
        height: 36px;
    }
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
}


.dashboard {
    position: fixed;
    top: 60px; 
    left: 0;
    width: 240px;
    height: calc(100vh - 60px);
    background: #153291;
    color: #ffffff;
    display: flex;
    flex-direction: column;
    padding: 1.5rem 1rem;
    box-shadow: 2px 0 10px rgba(0,0,0,0.3);
    transition: left 0.3s ease, width 0.3s ease;
    z-index: 1000;
}


#dash {
    font-size: 1.3rem;
    font-weight: bold;
    margin-bottom: 2rem;
    text-align: center;
    color: #f9f9f9;
    letter-spacing: 1px;
}


.dashboard a {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 0.8rem 1rem;
    border-radius: 8px;
    color: #d1d1d1;
    text-decoration: none;
    font-size: 0.95rem;
    transition: all 0.3s ease;
}

.dashboard a:hover {
    background: #ff8800fb;
    color: #ffffff;
    transform: translateX(4px);
}

.dashboard a.active {
    background: #4f46e5;
    color: #ffffff;
    font-weight: 600;
}

.dashboard .icon {
    width: 22px;
    height: 22px;
    flex-shrink: 0;
}

/* Top Bar */
.top_bar {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 60px;
  background: #ffffff;
  color: #1e1e2f;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 1rem;
  box-shadow: 0 2px 6px rgba(0,0,0,0.1);
  z-index: 1600;
}


.top_bar .left {
  display: flex;
  align-items: center;
  gap: 1rem;
}


#toggle_btn {
  font-size: 1.6rem;
  background: none;
  border: none;
  color: #1e1e2f;
  cursor: pointer;
  padding: 0.4rem;
  border-radius: 6px;
  transition: background 0.3s ease;
}

#toggle_btn:hover {
  background: rgba(0,0,0,0.05);
}


.logo {
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.logo .icon1 {
  width: 28px;
  height: 28px;
  color: #ff8800fb;
}

.heading {
  font-size: 1.3rem;
  font-weight: 700;
  margin: 0;
  color: #153291;
  letter-spacing: 0.5px;
}

.top_bar .role {
  font-size: 1.1rem;
  font-weight: 600;
  margin: 0;
  color: #1e1e2f;
}



.main_content {
    margin-top: 60px;      
    margin-left: 240px;   
    padding: 1rem;
    background: #f9f9f9;
    min-height: calc(100vh - 60px);
    transition: margin-left 0.3s ease;
}


.footer {
    background: #333;
    color: #fff;
    text-align: center;
    padding: 1rem;
    font-size: 0.9rem;
}


.dashboard.hidden {
    left: -240px;
}

.main_content.expanded {
    margin-left: 0;
}

.logout button {
  background: #e63946;
  border: none;
  color: #fff;
  padding: 8px 16px;
  border-radius: 6px;
  display: flex;
  align-items: center;
  gap: 8px;
  cursor: pointer;
  transition: background 0.3s ease;
}

.logout button:hover {
  background: #d62828;
}

@media (max-width: 768px) {
    .dashboard {
        top: 0;
        left: -240px;
        height: 100vh;
        box-shadow: 2px 0 12px rgba(0,0,0,0.5);
        z-index: 1500;   
    }

    .dashboard.active {
        left: 0;        
    }

    .main_content {
        margin-left: 0 !important;
        width: 100%;               
        margin-top: 60px;     
        position: relative; 
    }

    .top_bar {
        height: 55px;
        padding: 0 0.8rem;
    }

    #toggle_btn {
        font-size: 1.4rem;
    }

    .top_bar h4 {
        font-size: 1rem;
    }
}
